import { Component, OnInit } from "@angular/core";
import fetchFromSpotify, { request } from "../../services/api";
import { AppComponent } from '../app.component';


const AUTH_ENDPOINT =
  "https://nuod0t2zoe.execute-api.us-east-2.amazonaws.com/FT-Classroom/spotify-auth-token";
const TOKEN_KEY = "whos-who-access-token";

@Component({
  selector: "app-home",
  templateUrl: "./home.component.html",
  styleUrls: ["./home.component.css"],
})
export class HomeComponent implements OnInit {
  constructor(private appComponent: AppComponent) {}

  genres: String[] = ["House", "Alternative", "J-Rock", "R&B"];
  genreKeyPairs: { [key: string]: string } = {
    'rock': '37i9dQZF1DWXRqgorJj26U?si=4c6f4740439b4da8',
    'rap': '37i9dQZF1DX0XUsuxWHRQd?si=7850ab6551f44e44',
    'pop': '37i9dQZF1DXcBWIGoYBM5M?si=273655aab9a74df0',
    'country': '37i9dQZF1EQmPV0vrce2QZ?si=c2a96810183449bf',
    'hip-hop': '37i9dQZF1EQnqst5TRi17F?si=f7ad3110a2744a34',
    'jazz': '37i9dQZF1DXbITWG1ZJKYt?si=051ddd188e624076',
    'alternative': '37i9dQZF1DX9GRpeH4CL0S',
    'j-pop': '37i9dQZF1DXdbRLJPSmnyq',
    'k-pop': '3Ir5YWemOTGRRfXgROrsDV?si=08377f2607934049',
    'emo': '37i9dQZF1EIhAUAaVF0Kop?si=b2e1320d1dd74c7d'
  };
  selectedGenre: String = "";
  authLoading: boolean = false;
  configLoading: boolean = false;
  token: String = "";
  local_SelectedLevel = "";

  ngOnInit(): void {
    this.authLoading = true;
    const storedTokenString = localStorage.getItem(TOKEN_KEY);
    if (storedTokenString) {
      const storedToken = JSON.parse(storedTokenString);
      if (storedToken.expiration > Date.now()) {
        console.log("Token found in localstorage");
        this.authLoading = false;
        this.token = storedToken.value;
        this.loadGenres(storedToken.value);
        return;
      }
    }
    console.log("Sending request to AWS endpoint");
    request(AUTH_ENDPOINT).then(({ access_token, expires_in }) => {
      const newToken = {
        value: access_token,
        expiration: Date.now() + (expires_in - 20) * 1000,
      };
      localStorage.setItem(TOKEN_KEY, JSON.stringify(newToken));
      this.authLoading = false;
      this.token = newToken.value;
      this.loadGenres(newToken.value);
    });
  }

  loadGenres = async (t: any) => {
    this.configLoading = true;

    // #################################################################################
    // DEPRECATED!!! Use only for example purposes
    // DO NOT USE the recommendations endpoint in your application
    // Has been known to cause 429 errors
    // const response = await fetchFromSpotify({
    //   token: t,
    //   endpoint: "recommendations/available-genre-seeds",
    // });
    // console.log(response);
    // #################################################################################
    
     this.genres = [
       "rock",
       "rap",
       "pop",
       "country",
       "hip-hop",
       "jazz",
       "alternative",
       "j-pop",
       "k-pop",
       "emo"
     ]
    this.configLoading = false;
  };

  //Note* make play list dynamic 
  playlistRequest: string = "";
  songList: object[] = [];

  async setGenre(selectedGenre: any) {
    this.selectedGenre = selectedGenre;
    console.log(this.selectedGenre);
    if (this.genreKeyPairs.hasOwnProperty(selectedGenre)) {
      this.playlistRequest = "playlists/" + this.genreKeyPairs[selectedGenre];
    } 
    else {
      console.log("PLAYLIST NOT FOUND");
    }

    const response = await fetchFromSpotify({
      token: this.token,
      endpoint: this.playlistRequest
    });
    console.log("RESPONSE:");
    console.log(response);

    if (response.hasOwnProperty("tracks")) {
      if (response.tracks.hasOwnProperty("items")) {
        this.songList = response.tracks.items;
        console.log(this.songList);
      } 
      else {
        console.log("ITEMS NOT FOUND");
      }
    } 
    else {
      console.log("TRACKS NOT FOUND");
    }

    this.shuffleArray(this.songList); // Mutating shuffle

    // PRINT SONG #1
    console.log((this.songList[0] as any).track.name); // not runtime safe but avoids needing an interface
    // console.log(response.tracks.items[0].track.name); // parsing straight from Response
    
    // PRINT PREVIEW URL #1
    console.log(response.tracks.items[0].track.preview_url);

  }

  onLevelChange() {
    // Set isHard based on the selected level
    if (this.local_SelectedLevel === 'easy') {
      this.appComponent.selectedLevel = 'easy';
      this.appComponent.isHard = false;
    } else if (this.local_SelectedLevel === 'hard') {
      this.appComponent.selectedLevel = 'hard';
      this.appComponent.isHard = true;
    }
  }

  onPlayButtonClick() {
    // RESET Game logic variables
    this.appComponent.lostHardMode = false; // This prevents a previous loss state from continuing into the next game
    this.appComponent.wrongAnswers = 0;
    this.appComponent.count = 0;
    console.log("lostHardMode: " + this.appComponent.lostHardMode);
    console.log("selectedLevel: " + this.appComponent.selectedLevel);
    console.log("isHard: " + this.appComponent.isHard);
    console.log("wrongAnswers: " + this.appComponent.wrongAnswers);
    console.log("count: " + this.appComponent.count);
  }

  shuffleArray(array: object[]): void {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
    //Needed to iniatlized here because I couldn't inject HomeComponent
    this.appComponent.songList=array;
  }

  hardModeLeaderboard(): void{
    this.appComponent.isHard = true;
  }
}
<div class="background"></div>
<div class="container">
  <div class="box-container">
    <div class="title">
      <h1>Welcome to TTT!</h1>
      <h1>Tune Trivia Thrill</h1>
    </div>
    <div class="box-container-inner">
      <div *ngIf="authLoading || configLoading">Loading...</div>
      <div class="choose-your-genre"*ngIf="!authLoading && !configLoading">
        <h1>Choose Your Genre</h1>
        <select id="genres" [(ngModel)]="selectedGenre" (ngModelChange)="setGenre($event)">
          <option [value]=""></option>
          <option [value]="genre" *ngFor="let genre of genres">
            {{ genre }}
          </option>
        </select>
      </div>
      <div class="easy-or-hard">
        <h1>Choose Your Level</h1>
        <select name="level" id="level" [(ngModel)]="local_SelectedLevel" (change)="onLevelChange()">
          <option value=""></option>
          <option value="easy">Easy</option>
          <option value="hard">Hard</option>
        </select>
      </div>
      <div class="play">
        <a routerLink="/play-game">
          <button class="play-btn" (click)="onPlayButtonClick()">Play</button>  
        </a>
      </div>
      <div class="leader-board">
        <a routerLink="/leader-board">
          <button class="leader-board-btn">
            Leader Board<br>
            Easy
          </button> 
        </a>
        <a routerLink="/leader-board">
          <button class="leader-board-btn">
            Leader Board<br>
            Hard
          </button> 
        </a>
      </div>
    </div>
  </div>
</div>
